    "\U6e56\U5357\U7701" = "\U6e56\U5357\U7701\U5b9e\U65bd\U5de5\U4f24\U4fdd\U9669\U6761\U4f8b\U529e\U6cd5\n\U603b\U5219\n\n\n\U3000\U3000\U7b2c\U4e00\U6761 \U6839\U636e\U56fd\U52a1\U9662\U300a\U5de5\U4f24\U4fdd\U9669\U6761\U4f8b\U300b(\U4ee5\U4e0b\U7b80\U79f0\U300a\U6761\U4f8b\U300b)\U548c\U6709\U5173\U6cd5\U5f8b\U3001\U6cd5\U89c4\Uff0c\U7ed3\U5408\U672c\U7701\U5b9e\U9645\Uff0c\U5236\U5b9a\U672c\U529e\U6cd5\U3002\n\U3000\U3000\U672c\U7701\U884c\U653f\U533a\U57df\U5185\U7684\U4f01\U4e1a\U3001\U4e8b\U4e1a\U5355\U4f4d\U3001\U793e\U4f1a\U56e2\U4f53\U3001\U6c11\U529e\U975e\U4f01\U4e1a\U5355\U4f4d\U3001\U57fa\U91d1\U4f1a\U3001\U5f8b\U5e08\U4e8b\U52a1\U6240\U3001\U4f1a\U8ba1\U5e08\U4e8b\U52a1\U6240\U7b49\U7ec4\U7ec7\U548c\U6709\U96c7\U5de5\U7684\U4e2a\U4f53\U5de5\U5546\U6237\Uff08\U4ee5\U4e0b\U7edf\U79f0\U7528\U4eba\U5355\U4f4d\Uff09\Uff0c\U5e94\U5f53\U4f9d\U7167\U300a\U6761\U4f8b\U300b\U548c\U672c\U529e\U6cd5\U89c4\U5b9a\U53c2\U52a0\U5de5\U4f24\U4fdd\U9669\Uff0c\U4e3a\U672c\U5355\U4f4d\U5168\U90e8\U804c\U5de5\U6216\U8005\U96c7\U5de5\Uff08\U4ee5\U4e0b\U7edf\U79f0\U804c\U5de5\Uff09\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U3002\n\U3000\U3000\U7b2c\U4e8c\U6761 \U672c\U7701\U884c\U653f\U533a\U57df\U5185\U7684\U804c\U5de5\U5747\U6709\U4f9d\U7167\U300a\U6761\U4f8b\U300b\U548c\U672c\U529e\U6cd5\U7684\U89c4\U5b9a\U4eab\U53d7\U5de5\U4f24\U4fdd\U9669\U5f85\U9047\U7684\U6743\U5229\U3002\n\U3000\U3000\U7b2c\U4e09\U6761 \U7528\U4eba\U5355\U4f4d\U5e94\U5f53\U5728\U672c\U5355\U4f4d\U5185\U7684\U663e\U8457\U4f4d\U7f6e\U516c\U793a\U672c\U5355\U4f4d\U53c2\U52a0\U5de5\U4f24\U4fdd\U9669\U7684\U804c\U5de5\U540d\U5355\U3001\U7f34\U8d39\U5de5\U8d44\U989d\U3001\U5de5\U4f24\U4e8b\U6545\U548c\U5de5\U4f24\U7533\U62a5\U7b49\U60c5\U51b5\U3002\n\U3000\U3000\U7528\U4eba\U5355\U4f4d\U5e94\U5f53\U52a0\U5f3a\U5b89\U5168\U751f\U4ea7\U6559\U80b2\Uff0c\U52a0\U5f3a\U4e8b\U6545\U81ea\U6551\U4e92\U6551\U77e5\U8bc6\U548c\U6280\U80fd\U57f9\U8bad\Uff0c\U589e\U5f3a\U804c\U5de5\U9884\U9632\U5de5\U4f24\U7684\U610f\U8bc6\U548c\U81ea\U6551\U4e92\U6551\U80fd\U529b\U3002\n\U3000\U3000\U53d1\U751f\U5de5\U4f24\U65f6\Uff0c\U7528\U4eba\U5355\U4f4d\U5e94\U5f53\U91c7\U53d6\U63aa\U65bd\U4f7f\U5de5\U4f24\U804c\U5de5\U5f97\U5230\U53ca\U65f6\U6551\U6cbb\Uff0c\U5e76\U62a5\U544a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U548c\U5de5\U4f24\U4fdd\U9669\U7ecf\U529e\U673a\U6784\U3002\n\U3000\U3000\U7b2c\U56db\U6761 \U53bf\U7ea7\U4ee5\U4e0a\U4eba\U6c11\U653f\U5e9c\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U8d1f\U8d23\U672c\U884c\U653f\U533a\U57df\U5185\U7684\U5de5\U4f24\U4fdd\U9669\U5de5\U4f5c\Uff0c\U5176\U5de5\U4f24\U4fdd\U9669\U7ecf\U529e\U673a\U6784(\U4ee5\U4e0b\U79f0\U7ecf\U529e\U673a\U6784)\U5177\U4f53\U627f\U529e\U5de5\U4f24\U4fdd\U9669\U4e8b\U52a1\U3002\n\U7b2c\U4e8c\U7ae0 \U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\n\n\U7b2c\U4e94\U6761 \U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U7531\U4e0b\U5217\U5404\U9879\U6784\U6210\Uff1a\n(\U4e00)\U7528\U4eba\U5355\U4f4d\U7f34\U7eb3\U7684\U5de5\U4f24\U4fdd\U9669\U8d39,\n(\U4e8c)\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U7684\U5229\U606f,\n(\U4e09)\U5ef6\U8fdf\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U7684\U6ede\U7eb3\U91d1,\n(\U56db)\U653f\U5e9c\U5728\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8\U4e0d\U8db3\U65f6\U4f9d\U6cd5\U7ed9\U4e0e\U7684\U8865\U8d34,\n(\U4e94)\U5176\U4ed6\U4f9d\U6cd5\U7eb3\U5165\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U7684\U5176\U4ed6\U8d44\U91d1\U3002\n\U7b2c\U516d\U6761 \U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U5728\U8bbe\U533a\U7684\U5e02\U3001\U81ea\U6cbb\U5dde\U5b9e\U884c\U5168\U5e02\U3001\U5dde\U7edf\U7b79\Uff0c\U6761\U4ef6\U5177\U5907\U65f6\U5b9e\U884c\U5168\U7701\U7edf\U7b79\U3002\n\U5de5\U4f24\U4fdd\U9669\U5168\U7701\U7edf\U7b79\U524d\Uff0c\U517b\U8001\U4fdd\U9669\U7531\U7701\U76f4\U63a5\U7ba1\U7406\U7684\U7528\U4eba\U5355\U4f4d\Uff0c\U5176\U5de5\U4f24\U4fdd\U9669\U53ef\U4ee5\U7531\U7701\U76f4\U63a5\U7ba1\U7406\U3002\n\U7b2c\U4e03\U6761 \U7edf\U7b79\U5730\U533a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U5e94\U5f53\U6309\U7167\U56fd\U5bb6\U89c4\U5b9a\Uff0c\U63d0\U51fa\U9002\U7528\U884c\U4e1a\U5dee\U522b\U8d39\U7387\U53ca\U884c\U4e1a\U5185\U8d39\U7387\U6863\U6b21\U7684\U5177\U4f53\U65b9\U6848\Uff0c\U62a5\U7edf\U7b79\U5730\U533a\U4eba\U6c11\U653f\U5e9c\U6279\U51c6\U540e\U5b9e\U65bd\U3002\n\U7ecf\U529e\U673a\U6784\U5e94\U5f53\U6839\U636e\U7edf\U7b79\U5730\U533a\U4eba\U6c11\U653f\U5e9c\U6279\U51c6\U7684\U7528\U4eba\U5355\U4f4d\U6240\U5c5e\U884c\U4e1a\U5dee\U522b\U8d39\U7387\U53ca\U884c\U4e1a\U5185\U8d39\U7387\U6863\U6b21\Uff0c\U786e\U5b9a\U7528\U4eba\U5355\U4f4d\U7684\U7f34\U8d39\U6570\U989d\U3002\n\U7b2c\U516b\U6761 \U7528\U4eba\U5355\U4f4d\U5e94\U6309\U7167\U7ecf\U529e\U673a\U6784\U786e\U5b9a\U7684\U7f34\U8d39\U6570\U989d\U6309\U65f6\U8db3\U989d\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U3002\n\U96be\U4ee5\U6309\U7167\U804c\U5de5\U5de5\U8d44\U603b\U989d\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U7684\U884c\U4e1a\U548c\U4f01\U4e1a\Uff0c\U7ecf\U7edf\U7b79\U5730\U533a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U6838\U5b9a\Uff0c\U53ef\U4ee5\U6309\U7167\U56fd\U52a1\U9662\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U89c4\U5b9a\U7684\U7f34\U8d39\U529e\U6cd5\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U3002\n\U7b2c\U4e5d\U6761 \U4e0b\U5217\U56e0\U5de5\U4f24\U53d1\U751f\U7684\U8d39\U7528\U548c\U7528\U4e8e\U5de5\U4f24\U4fdd\U9669\U7684\U8d39\U7528\Uff0c\U4f9d\U7167\U56fd\U5bb6\U89c4\U5b9a\U4ece\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8\Uff1a\n(\U4e00)\U6cbb\U7597\U5de5\U4f24\U7684\U533b\U7597\U8d39\U7528\U548c\U5eb7\U590d\U8d39\U7528,\n(\U4e8c)\U4f4f\U9662\U4f19\U98df\U8865\U52a9\U8d39,\n(\U4e09)\U5230\U7edf\U7b79\U5730\U533a\U4ee5\U5916\U5c31\U533b\U7684\U4ea4\U901a\U98df\U5bbf\U8d39,\n(\U56db)\U5b89\U88c5\U914d\U7f6e\U4f24\U6b8b\U8f85\U52a9\U5668\U5177\U6240\U9700\U8d39\U7528,\n(\U4e94)\U751f\U6d3b\U4e0d\U80fd\U81ea\U7406\U7684\Uff0c\U7ecf\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U786e\U8ba4\U7684\U751f\U6d3b\U62a4\U7406\U8d39,\n(\U516d)\U4e00\U6b21\U6027\U4f24\U6b8b\U8865\U52a9\U91d1,\n(\U4e03)\U4e00\U7ea7\U81f3\U56db\U7ea7\U5de5\U4f24\U4eba\U5458\U4f24\U6b8b\U6d25\U8d34,\n(\U516b)\U7ec8\U6b62\U6216\U8005\U89e3\U9664\U52b3\U52a8(\U8058\U7528)\U5408\U540c\U65f6\U5e94\U5f53\U4eab\U53d7\U7684\U4e00\U6b21\U6027\U533b\U7597\U8865\U52a9\U91d1,\n(\U4e5d)\U56e0\U5de5\U6b7b\U4ea1\U804c\U5de5\U7684\U9057\U5c5e\U9886\U53d6\U7684\U4e27\U846c\U8865\U52a9\U91d1\U3001\U4f9b\U517b\U4eb2\U5c5e\U629a\U6064\U91d1\U3001\U4e00\U6b21\U6027\U5de5\U4ea1\U8865\U52a9\U91d1,\n(\U5341)\U5de5\U4f24\U8ba4\U5b9a\U8c03\U67e5\U548c\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U8d39\U7528,\n(\U5341\U4e00)\U5de5\U4f24\U9884\U9632\U5ba3\U4f20\U548c\U57f9\U8bad\U8d39\U7528\U3002\n\U7b2c\U5341\U6761 \U4e0b\U5217\U8d39\U7528\Uff0c\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U4e0d\U4e88\U652f\U4ed8\Uff1a\n(\U4e00)\U5e94\U5f53\U4ece\U533b\U7597\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8\U7684\U8d39\U7528,\n(\U4e8c)\U5883\U5916\U6cbb\U7597\U8d39\U7528,\n(\U4e09)\U4e0d\U7b26\U5408\U5de5\U4f24\U4fdd\U9669\U8bca\U7597\U9879\U76ee\U76ee\U5f55\U3001\U5de5\U4f24\U4fdd\U9669\U836f\U54c1\U76ee\U5f55\U3001\U5de5\U4f24\U4fdd\U9669\U4f4f\U9662\U670d\U52a1\U6807\U51c6\U7684\U8d39\U7528,\n(\U56db)\U4f9d\U6cd5\U4e0d\U4e88\U652f\U4ed8\U7684\U5176\U4ed6\U8d39\U7528\U3002\n\U7b2c\U5341\U4e00\U6761 \U5efa\U7acb\U7701\U548c\U8bbe\U533a\U7684\U5e02\U3001\U81ea\U6cbb\U5dde\U4e24\U7ea7\U5de5\U4f24\U4fdd\U9669\U50a8\U5907\U91d1\U5236\U5ea6\U3002\U7edf\U7b79\U5730\U533a\U6309\U7167\U5f53\U5e74\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U5f81\U7f34\U603b\U989d\U768410%\U63d0\U53d6\U50a8\U5907\U91d1\U3002\U81ea\U75595%\Uff0c\U5411\U7701\U4e0a\U89e35%\U3002\n\U5de5\U4f24\U4fdd\U9669\U50a8\U5907\U91d1\U7528\U4e8e\U91cd\U5927\U4e8b\U6545\U7684\U5de5\U4f24\U4fdd\U9669\U5f85\U9047\U652f\U4ed8\U53ca\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U5165\U4e0d\U6577\U51fa\U65f6\U7684\U652f\U4ed8\U3002\U7701\U7ea7\U5de5\U4f24\U4fdd\U9669\U50a8\U5907\U91d1\U7528\U4e8e\U7279\U5927\U4e8b\U6545\U548c\U7edf\U7b79\U5730\U533a\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U5165\U4e0d\U6577\U51fa\U65f6\U7684\U8c03\U5242\U3002\U5de5\U4f24\U4fdd\U9669\U50a8\U5907\U91d1\U7684\U5177\U4f53\U4f7f\U7528\U529e\U6cd5\U7531\U7701\U4eba\U6c11\U653f\U5e9c\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U4f1a\U540c\U8d22\U653f\U90e8\U95e8\U5236\U5b9a\Uff0c\U62a5\U7701\U4eba\U6c11\U653f\U5e9c\U6279\U51c6\U540e\U6267\U884c\U3002\n\U7b2c\U4e09\U7ae0 \U5de5\U4f24\U8ba4\U5b9a\n\n\U7b2c\U5341\U4e8c\U6761 \U7edf\U7b79\U5730\U533a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U548c\U7ecf\U529e\U673a\U6784\U5e94\U5f53\U5efa\U7acb\U5de5\U4f24\U4e8b\U6545\U62a5\U544a\U5236\U5ea6\U3002\n\U4e0a\U4e0b\U73ed\U9014\U4e2d\U7684\U4ea4\U901a\U4e8b\U6545\Uff0c\U6216\U8005\U5176\U4ed6\U539f\U56e0\U9020\U6210\U7684\U91cd\U4f24\U4e8b\U6545\U3001\U6b7b\U4ea1\U4e8b\U6545\Uff0c\U7528\U4eba\U5355\U4f4d\U5e94\U5f53\U4e8e\U4e8b\U6545\U53d1\U751f\U540e24\U5c0f\U65f6\U5185\U62a5\U544a,\U5176\U4ed6\U539f\U56e0\U9020\U6210\U7684\U8f7b\U4f24\U4e8b\U6545\U548c\U804c\U4e1a\U75c5\Uff0c\U7528\U4eba\U5355\U4f4d\U5e94\U5f53\U4e8e\U4e8b\U6545\U53d1\U751f\U540e\U6216\U8005\U63a5\U5230\U804c\U4e1a\U75c5\U8bca\U65ad\U4e663\U65e5\U5185\U62a5\U544a,\U56e0\U4e0d\U53ef\U6297\U529b\U539f\U56e0\U65e0\U6cd5\U5728\U4e0a\U8ff0\U65f6\U95f4\U5185\U62a5\U544a\U7684\Uff0c\U5e94\U5f53\U4e8e\U969c\U788d\U6d88\U9664\U540e3\U65e5\U5185\U62a5\U544a\U3002\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U548c\U7ecf\U529e\U673a\U6784\U5e94\U5f53\U5efa\U7acb\U53f0\U5e10\Uff0c\U4e88\U4ee5\U767b\U8bb0\U3002\n\U7b2c\U5341\U4e09\U6761 \U7528\U4eba\U5355\U4f4d\U3001\U5de5\U4f24\U804c\U5de5\U6216\U8005\U5176\U8fd1\U4eb2\U5c5e\U3001\U5de5\U4f1a\U7ec4\U7ec7\U7533\U8bf7\U5de5\U4f24\U8ba4\U5b9a\U7684\Uff0c\U5e94\U5f53\U5728\U300a\U6761\U4f8b\U300b\U7b2c\U5341\U4e03\U6761\U89c4\U5b9a\U671f\U9650\U5185\U5411\U7edf\U7b79\U5730\U533a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U63d0\U51fa\U3002\n\U7528\U4eba\U5355\U4f4d\U56e0\U7279\U6b8a\U539f\U56e0\U4e0d\U80fd\U5728\U300a\U6761\U4f8b\U300b\U7b2c\U5341\U4e03\U6761\U89c4\U5b9a\U671f\U9650\U5185\U63d0\U51fa\U7533\U8bf7\U7684\Uff0c\U7ecf\U7edf\U7b79\U5730\U533a\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U540c\U610f\Uff0c\U7533\U8bf7\U671f\U9650\U53ef\U4ee5\U5ef6\U957f60\U65e5\U3002\n\U7528\U4eba\U5355\U4f4d\U672a\U5728\U524d\U4e24\U6b3e\U89c4\U5b9a\U7684\U671f\U9650\U5185\U63d0\U51fa\U7533\U8bf7\U7684\Uff0c\U5728\U6b64\U671f\U95f4\U53d1\U751f\U7684\U5de5\U4f24\U5f85\U9047\U8d39\U7528\U7531\U7528\U4eba\U5355\U4f4d\U8d1f\U62c5\U3002\n\U7b2c\U5341\U56db\U6761 \U7533\U8bf7\U4eba\U7533\U8bf7\U5de5\U4f24\U8ba4\U5b9a\Uff0c\U5e94\U5f53\U586b\U62a5\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U8868\U5e76\U63d0\U4ea4\U4e0b\U5217\U76f8\U5173\U6750\U6599\Uff1a\n(\U4e00) \U53d7\U4f24\U5bb3\U804c\U5de5\U7684\U8eab\U4efd\U8bc1\U660e,\n(\U4e8c) \U4e0e\U7528\U4eba\U5355\U4f4d\U5b58\U5728\U52b3\U52a8\U5173\U7cfb(\U5305\U62ec\U4eba\U4e8b\U5173\U7cfb)\U7684\U8bc1\U660e\U6750\U6599,\n(\U4e09) \U533b\U7597\U8bca\U65ad\U8bc1\U660e\Uff0c\U6216\U8005\U804c\U4e1a\U75c5\U8bca\U65ad\U8bc1\U660e\U4e66\U6216\U8005\U804c\U4e1a\U75c5\U8bca\U65ad\U9274\U5b9a\U4e66,\n(\U56db) \U5728\U5de5\U4f5c\U65f6\U95f4\U548c\U5de5\U4f5c\U573a\U6240\U5185\Uff0c\U56e0\U5c65\U884c\U5de5\U4f5c\U804c\U8d23\U53d7\U5230\U66b4\U529b\U7b49\U610f\U5916\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U4eba\U6c11\U6cd5\U9662\U88c1\U5224\U6587\U4e66\U6216\U8005\U516c\U5b89\U90e8\U95e8\U7684\U8bc1\U660e\U6216\U8005\U5176\U4ed6\U8bc1\U660e,\n(\U4e94) \U5728\U4e0a\U4e0b\U73ed\U9014\U4e2d\Uff0c\U53d7\U5230\U975e\U672c\U4eba\U4e3b\U8981\U8d23\U4efb\U7684\U4ea4\U901a\U4e8b\U6545\U6216\U8005\U57ce\U5e02\U8f68\U9053\U4ea4\U901a\U3001\U5ba2\U8fd0\U8f6e\U6e21\U3001\U706b\U8f66\U4e8b\U6545\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U516c\U5b89\U4ea4\U901a\U7ba1\U7406\U7b49\U90e8\U95e8\U6216\U8005\U5176\U4ed6\U76f8\U5173\U90e8\U95e8\U7684\U8bc1\U660e,\n(\U516d) \U56e0\U5de5\U5916\U51fa\U671f\U95f4\Uff0c\U7531\U4e8e\U5de5\U4f5c\U539f\U56e0\U53d7\U5230\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U516c\U5b89\U90e8\U95e8\U7684\U8bc1\U660e\U6216\U8005\U5176\U4ed6\U8bc1\U660e,\U56e0\U53d1\U751f\U4e8b\U6545\U4e0b\U843d\U4e0d\U660e\Uff0c\U63d0\U51fa\U56e0\U5de5\U6b7b\U4ea1\U8ba4\U5b9a\U7533\U8bf7\U7684\Uff0c\U63d0\U4ea4\U4eba\U6c11\U6cd5\U9662\U5ba3\U544a\U6b7b\U4ea1\U7684\U6587\U4e66,\n(\U4e03) \U5728\U5de5\U4f5c\U65f6\U95f4\U3001\U5de5\U4f5c\U5c97\U4f4d\U7a81\U53d1\U75be\U75c5\U6b7b\U4ea1\Uff0c\U6216\U8005\U5728\U5de5\U4f5c\U65f6\U95f4\U3001\U5de5\U4f5c\U5c97\U4f4d\U7a81\U53d1\U75be\U75c5\U7ecf\U62a2\U6551\U65e0\U654848\U5c0f\U65f6\U5185\U6b7b\U4ea1\U7684\Uff0c\U63d0\U4ea4\U533b\U7597\U673a\U6784\U7684\U62a2\U6551\U548c\U6b7b\U4ea1\U8bc1\U660e,\n(\U516b) \U5728\U62a2\U9669\U6551\U707e\U7b49\U7ef4\U62a4\U56fd\U5bb6\U5229\U76ca\U3001\U516c\U5171\U5229\U76ca\U6d3b\U52a8\U4e2d\U53d7\U5230\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U6c11\U653f\U90e8\U95e8\U6216\U8005\U5176\U4ed6\U76f8\U5173\U90e8\U95e8\U7684\U8bc1\U660e,\n(\U4e5d) \U56e0\U6218\U3001\U56e0\U516c\U8d1f\U4f24\U81f4\U6b8b\U7684\U8f6c\U4e1a\U3001\U590d\U5458\U3001\U9000\U4f0d\U519b\U4eba\Uff0c\U5230\U7528\U4eba\U5355\U4f4d\U540e\U65e7\U4f24\U590d\U53d1\U7684\Uff0c\U63d0\U4ea4\U9769\U547d\U4f24\U6b8b\U519b\U4eba\U8bc1\U53ca\U53bf\U7ea7\U4ee5\U4e0a\U533b\U7597\U673a\U6784\U7684\U65e7\U4f24\U590d\U53d1\U8bca\U65ad\U8bc1\U660e\U3002\n\U7528\U4eba\U5355\U4f4d\U672a\U53c2\U52a0\U5de5\U4f24\U4fdd\U9669\U7684\Uff0c\U8fd8\U5e94\U5f53\U63d0\U4ea4\U7528\U4eba\U5355\U4f4d\U7684\U8bbe\U7acb\U767b\U8bb0\U6216\U8005\U8bbe\U7acb\U6279\U51c6\U8bc1\U660e\U3002\n\U7b2c\U5341\U4e94\U6761 \U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U6536\U5230\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U540e\Uff0c\U5e94\U5f53\U572815\U65e5\U5185\U5bf9\U7533\U8bf7\U4eba\U63d0\U4ea4\U7684\U6750\U6599\U8fdb\U884c\U5ba1\U6838\U3002\U6750\U6599\U5b8c\U6574\U7684\Uff0c\U4f5c\U51fa\U53d7\U7406\U6216\U8005\U4e0d\U4e88\U53d7\U7406\U7684\U51b3\U5b9a\U3002\U51b3\U5b9a\U53d7\U7406\U7684\Uff0c\U5e94\U5f53\U51fa\U5177\U300a\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U53d7\U7406\U51b3\U5b9a\U4e66\U300b,\U51b3\U5b9a\U4e0d\U4e88\U53d7\U7406\U7684\Uff0c\U5e94\U5f53\U51fa\U5177\U300a\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U4e0d\U4e88\U53d7\U7406\U51b3\U5b9a\U4e66\U300b\U3002\n\U7533\U8bf7\U4eba\U63d0\U4f9b\U7684\U6750\U6599\U4e0d\U5b8c\U6574\U7684\Uff0c\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U5e94\U5f53\U5f53\U573a\U6216\U8005\U4e8e5\U65e5\U5185\U4e00\U6b21\U6027\U4e66\U9762\U544a\U77e5\U7533\U8bf7\U4eba\U9700\U8981\U8865\U6b63\U7684\U5168\U90e8\U6750\U6599\U3002\U903e\U671f\U672a\U544a\U77e5\U7684\Uff0c\U6536\U5230\U6750\U6599\U4e4b\U65e5\U8d77\U5373\U4e3a\U53d7\U7406\U3002\U7533\U8bf7\U4eba\U63d0\U4ea4\U4e86\U5168\U90e8\U8865\U6b63\U6750\U6599\U7684\Uff0c\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U5e94\U5f53\U4e8e15\U65e5\U5185\U4f9d\U7167\U524d\U6b3e\U89c4\U5b9a\U4f5c\U51fa\U5e76\U51fa\U5177\U662f\U5426\U53d7\U7406\U7684\U51b3\U5b9a\U3002\n\U7b2c\U5341\U516d\U6761 \U7533\U8bf7\U4eba\U63d0\U51fa\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\Uff0c\U6709\U4e0b\U5217\U60c5\U5f62\U4e4b\U4e00\U7684\Uff0c\U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U4e0d\U4e88\U53d7\U7406\Uff0c\U5e76\U5e94\U5f53\U8bf4\U660e\U7406\U7531\Uff1a\n(\U4e00) \U4e0d\U7b26\U5408\U7ba1\U8f96\U6743\U89c4\U5b9a\U7684,\n(\U4e8c) \U5c5e\U4e8e\U300a\U6761\U4f8b\U300b\U7b2c\U516d\U5341\U516d\U6761\U89c4\U5b9a\U60c5\U5f62\U7684\U3002\n\U7b2c\U5341\U4e03\U6761 \U793e\U4f1a\U4fdd\U9669\U884c\U653f\U90e8\U95e8\U5e94\U5f53\U5728\U300a\U6761\U4f8b\U300b\U7b2c\U4e8c\U5341\U6761\U89c4\U5b9a\U7684\U65f6\U9650\U5185\U4f5c\U51fa\U5de5\U4f24\U8ba4\U5b9a\U51b3\U5b9a\Uff0c\U5e76\U81ea\U5de5\U4f24\U8ba4\U5b9a\U51b3\U5b9a\U4f5c\U51fa\U4e4b\U65e5\U8d7720\U65e5\U5185\Uff0c\U5c06\U5de5\U4f24\U8ba4\U5b9a\U51b3\U5b9a\U4e66\U9001\U8fbe\U53d7\U4f24\U5bb3\U804c\U5de5\U6216\U8005\U5176\U8fd1\U4eb2\U5c5e\U548c\U7528\U4eba\U5355\U4f4d\Uff0c\U5e76\U6284\U9001\U7ecf\U529e\U673a\U6784\U3002\n\U5de5\U4f24\U804c\U5de5\U4e0e\U7528\U4eba\U5355\U4f4d\U56e0\U662f\U5426\U5b58\U5728\U52b3\U52a8\U5173\U7cfb\U53d1\U751f\U4e89\U8bae\U7684