    "\U9655\U897f\U7701" = "\U9655\U897f\U7701\U5b9e\U65bd\U300a\U5de5\U4f24\U4fdd\U9669\U6761\U4f8b\U300b\U529e\U6cd5\n\U4e00\U81f3\U4e94\U6761\n\U7b2c\U4e00\U6761\U3000\U6839\U636e\U56fd\U52a1\U9662\U300a\U5de5\U4f24\U4fdd\U9669\U6761\U4f8b[1]\U00a0\U00a0\U300b(\U4ee5\U4e0b\U7b80\U79f0\U300a\U6761\U4f8b \U300b)\Uff0c\U7ed3\U5408\U672c\U7701\U5b9e\U9645\Uff0c\U5236\U5b9a\U672c\U529e\U6cd5\U3002\n\U7b2c\U4e8c\U6761\U3000\U672c\U7701\U884c\U653f\U533a\U57df\U5185\U7684\U5404\U7c7b\U4f01\U4e1a\U3001\U6709\U96c7\U5de5\U7684\U4e2a\U4f53\U5de5\U5546\U6237(\U4ee5\U4e0b\U7b80\U79f0 \U7528\U4eba\U5355\U4f4d)\U548c\U4e0e\U4e4b\U5f62\U6210\U52b3\U52a8\U5173\U7cfb\U7684\U52b3\U52a8\U8005(\U4ee5\U4e0b\U7b80\U79f0\U804c\U5de5)\Uff0c\U9002\U7528\U672c\U529e\U6cd5\U3002\n\U7b2c\U4e09\U6761\U3000\U53bf\U7ea7\U4ee5\U4e0a\U4eba\U6c11\U653f\U5e9c\U52b3\U52a8\U548c\U793e\U4f1a\U4fdd\U969c\U884c\U653f\U90e8\U95e8(\U4ee5\U4e0b\U7b80\U79f0\U52b3\U52a8\U4fdd \U969c\U884c\U653f\U90e8\U95e8)\U6309\U7167\U56fd\U52a1 \U9662\U6709\U5173\U89c4\U5b9a\U8bbe\U7acb\U7684\U5de5\U4f24\U4fdd\U9669\U7ecf\U529e\U673a\U6784(\U4ee5\U4e0b\U7b80\U79f0\U7ecf\U529e\U673a\U6784)\U7684\U5de5\U4f5c\U7ecf\U8d39\Uff0c\U5217\U5165\U672c\U7ea7\U8d22\U653f\U9884\U7b97\U3002\n\U7b2c\U56db\U6761\U3000\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U5728\U8bbe\U533a\U7684\U5e02\U5b9e\U884c\U5168\U5e02\U7edf\U7b79\Uff0c\U56fd\U5bb6\U53e6\U6709\U89c4\U5b9a\U7684\U9664\U5916 \U3002\n\U7b2c\U4e94\U6761\U3000\U5de5\U4f24\U4fdd\U9669\U8d39\U7531\U5730\U65b9\U7a0e\U52a1\U90e8\U95e8\U5f81\U6536\U3002\n\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U548c\U8d22\U653f\U3001\U5ba1\U8ba1\U884c\U653f\U90e8\U95e8\U4f9d\U7167\U300a\U6761\U4f8b\U300b\U548c\U672c\U529e\U6cd5\U7684\U89c4\U5b9a\Uff0c\U5bf9\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U7684 \U6536\U652f\U3001\U7ba1\U7406\U60c5\U51b5\U8fdb\U884c\U76d1\U7763\U3002\n\U516d\U81f3\U5341\U6761\n\U7b2c\U516d\U6761\U3000\U7ecf\U529e\U673a\U6784\U5e94\U5f53\U6839\U636e\U7528\U4eba\U5355\U4f4d\U7684\U5de5\U5546\U767b\U8bb0\U548c\U4e3b\U8981\U751f\U4ea7\U7ecf\U8425\U4e1a\U52a1\U7b49\U60c5\U51b5\Uff0c\U6309\U7167\U4e0d\U540c\U884c\U4e1a\U5dee \U522b\U8d39\U7387\U6807\U51c6\Uff0c\U786e\U5b9a\U7528\U4eba\U5355\U4f4d\U7684\U521d\U6b21\U7f34\U8d39\U8d39\U7387\U3002\U7528\U4eba\U5355\U4f4d\U7684\U751f\U4ea7\U7ecf\U8425\U8de8\U884c\U4e1a\U7684\Uff0c\U6309\U7167\U98ce\U9669\U8f83\U9ad8\U7684\U884c\U4e1a\U786e\U5b9a\U7f34\U8d39\U8d39\U7387\U3002\n\U7ecf\U529e\U673a\U6784\U5e94\U5f53\U6839\U636e\U7528\U4eba\U5355\U4f4d\U5de5\U4f24\U4fdd\U9669\U8d39\U4f7f\U7528\U3001\U5de5\U4f24\U53d1\U751f\U7387\U548c\U804c\U4e1a\U75c5\U5371\U5bb3\U7a0b\U5ea6\U7b49\U60c5\U51b5\Uff0c\U9002\U65f6\U63d0 \U51fa\U8c03\U6574\U7528\U4eba\U5355\U4f4d\U8d39\U7387\U7684\U5efa\U8bae\Uff0c\U7ecf\U7edf\U7b79\U5730\U533a\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U6279\U51c6\U540e\Uff0c\U5bf9\U7528\U4eba\U5355\U4f4d\U7684\U7f34\U8d39\U8d39\U7387\U8fdb\U884c\U8c03\U6574\U3002\n\U7b2c\U4e03\U6761\U3000\U5de5\U4f24\U4fdd\U9669\U8d39\U6309\U4e0b\U5217\U89c4\U5b9a\U7f34\U7eb3\Uff1a\n(\U4e00)\U7528\U4eba\U5355\U4f4d\U6309\U672c\U5355\U4f4d\U804c\U5de5\U4e0a\U5e74\U5ea6\U6708\U5e73\U5747\U5de5\U8d44\U603b\U989d\U4e58\U4ee5\U5355\U4f4d\U7f34\U8d39\U8d39\U7387\U4e4b\U79ef\U7f34\U7eb3\Uff0c\U804c\U5de5 \U4e2a\U4eba\U4e0d\U7f34\U8d39\U3002\n(\U4e8c)\U7528\U4eba\U5355\U4f4d\U804c\U5de5\U5e73\U5747\U5de5\U8d44\U4f4e\U4e8e\U6240\U5728\U5730\U4e0a\U5e74\U5ea6\U804c\U5de5\U5e73\U5747\U6708\U5de5\U8d4460%\U7684\Uff0c\U4ee5\U6240\U5728\U5730\U4e0a\U5e74\U5ea6 \U804c\U5de5\U5e73\U5747\U6708\U5de5\U8d44\U768460%\U4f5c\U4e3a\U7f34\U8d39\U57fa\U6570,\U804c\U5de5\U5e73\U5747\U5de5\U8d44\U9ad8\U4e8e\U6240\U5728\U5730\U4e0a\U5e74\U5ea6\U804c\U5de5\U5e73\U5747\U6708\U5de5\U8d44300% \U7684\Uff0c\U4ee5\U6240\U5728\U5730\U4e0a\U5e74\U5ea6\U804c\U5de5\U5e73\U5747\U6708\U5de5\U8d44300%\U4f5c\U4e3a\U7f34\U8d39\U57fa\U6570\Uff0c\U8d85\U8fc7\U90e8\U5206\U7528\U4eba\U5355\U4f4d\U4e0d\U518d\U7f34\U7eb3\U3002\n(\U4e09)\U6709\U96c7\U5de5\U7684\U4e2a\U4f53\U5de5\U5546\U6237\U4ee5\U6240\U5728\U5730\U4e0a\U5e74\U5ea6\U804c\U5de5\U6708\U5e73\U5747\U5de5\U8d44\U4e3a\U57fa\U6570\U7f34\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U3002\n\U7b2c\U516b\U6761\U3000\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U7528\U4e8e\U4e0b\U5217\U9879\U76ee\U652f\U51fa\Uff1a\n(\U4e00)\U5de5\U4f24\U533b\U7597\U8d39,\n(\U4e8c)\U4e00\U81f3\U56db\U7ea7\U5de5\U4f24\U4eba\U5458\U4f24\U6b8b\U6d25\U8d34,\n(\U4e09)\U4e00\U6b21\U6027\U4f24\U6b8b\U8865\U52a9\U91d1,\n(\U56db)\U751f\U6d3b\U62a4\U7406\U8d39,\n(\U4e94)\U4e27\U846c\U8865\U52a9\U91d1,\n(\U516d)\U4f9b\U517b\U4eb2\U5c5e\U629a\U6064\U91d1,\n(\U4e03)\U4e00\U6b21\U6027\U5de5\U4ea1\U8865\U52a9\U91d1,\n(\U516b)\U8f85\U52a9\U5668\U5177\U914d\U7f6e\U8d39,\n(\U4e5d)\U5de5\U4f24\U5eb7\U590d\U8d39,\n(\U5341)\U5de5\U4f24\U804c\U5de5\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U8d39,\n(\U5341\U4e00)\U6cd5\U5f8b\U3001\U6cd5\U89c4\U89c4\U5b9a\U7684\U5176\U4ed6\U8d39\U7528\U3002\n\U7b2c\U4e5d\U6761\U3000\U7528\U4eba\U5355\U4f4d\U6b20\U7f34\U5de5\U4f24\U4fdd\U9669\U8d39\U7684\Uff0c\U6b20\U7f34\U524d\U5df2\U7531\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8\U5de5 \U4f24\U4fdd\U9669\U5f85\U9047\U7684\U5de5\U4f24\U804c\U5de5 \U548c\U6b20\U7f34\U671f\U95f4\U53d1\U751f\U5de5\U4f24\U7684\U804c\U5de5\Uff0c\U5176\U6b20\U7f34\U671f\U95f4\U7684\U5de5\U4f24\U4fdd\U9669\U5f85\U9047\U7531\U7528\U4eba\U5355\U4f4d\U6309\U7167\U300a\U6761\U4f8b\U300b\U548c\U672c\U529e\U6cd5\U89c4\U5b9a\U7684\U9879\U76ee\U548c\U6807\U51c6\U652f\U4ed8\Uff0c\U8865\U7f34\U540e\U7531\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U8865\U652f\U3002\n\U7b2c\U5341\U6761\U3000\U7edf\U7b79\U5730\U533a\U5e94\U5f53\U4ece\U5f53\U5e74\U6536\U53d6\U7684\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U4e2d\U63d010%\U5efa\U7acb\U98ce\U9669\U50a8\U5907 \U91d1\Uff0c\U7528\U4e8e\U672c\U5730\U533a\U91cd\U5927 \U4e8b\U6545\U7684\U5de5\U4f24\U4fdd\U9669\U5f85\U9047\U652f\U4ed8\U3002\U98ce\U9669\U50a8\U5907\U91d1\U7684\U5177\U4f53\U4f7f\U7528\U529e\U6cd5\U7531\U7701\U4eba\U6c11\U653f\U5e9c\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U4f1a\U540c\U8d22\U653f\U884c\U653f\U90e8\U95e8\U53e6\U884c\U5236\U5b9a\U3002\n\U5341\U4e00\U81f3\U5341\U4e94\U6761\n\U7b2c\U5341\U4e00\U6761\U3000\U7533\U8bf7\U4eba\U5e94\U5f53\U4f9d\U7167\U300a\U6761\U4f8b\U300b\U548c\U300a\U5de5\U4f24\U8ba4\U5b9a\U529e\U6cd5\U300b\U7684\U89c4\U5b9a\Uff0c\U5411\U8fdb \U884c\U5de5\U4f24\U4fdd\U9669\U767b\U8bb0\U7684\U7edf\U7b79 \U5730\U533a\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U63d0\U51fa\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U6216\U8005\U89c6\U540c\U5de5\U4f24\U8ba4\U5b9a\U7533\U8bf7\U3002\U5c5e\U4e8e\U4e0b\U5217\U60c5\U51b5\U7684\U5e94\U5f53\U63d0\U4f9b\U76f8\U5173\U8bc1\U660e\U6750\U6599\Uff1a\n(\U4e00)\U7531\U4e8e\U4ea4\U901a\U4e8b\U6545\U53d7\U5230\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U516c\U5b89\U4ea4\U901a\U7ba1\U7406\U90e8\U95e8\U7684\U8d23\U4efb\U8ba4\U5b9a\U4e66\U6216\U8005\U5176\U4ed6\U6709\U6548\U7684\U6cd5\U5f8b \U6587\U4e66\U7b49\U76f8\U5173\U8bc1\U660e,\n(\U4e8c)\U5728\U62a2\U9669\U6551\U707e\U7b49\U7ef4\U62a4\U56fd\U5bb6\U5229\U76ca\U3001\U516c\U4f17\U5229\U76ca\U6d3b\U52a8\U4e2d\U53d7\U5230\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U4e8b\U53d1\U5730\U53bf\U7ea7\U4ee5\U4e0a\U4eba\U6c11 \U653f\U5e9c\U6709\U5173\U90e8\U95e8\U51fa\U5177\U7684\U8bc1\U660e,\n(\U4e09)\U56e0\U5c65\U884c\U5de5\U4f5c\U804c\U8d23\U53d7\U5230\U66b4\U529b\U4f24\U5bb3\U7684\Uff0c\U63d0\U4ea4\U516c\U5b89\U90e8\U95e8\U7684\U8bc1\U660e\U3001\U4eba\U6c11\U6cd5\U9662\U7684\U5224\U51b3\U4e66\U6216\U8005\U5176\U4ed6 \U8bc1\U660e,\n(\U56db)\U5c5e\U4e8e\U56e0\U516c\U3001\U56e0\U6218\U81f4\U6b8b\U7684\U8f6c\U4e1a\U3001\U590d\U5458\U9000\U4f0d\U519b\U4eba\U65e7\U4f24\U590d\U53d1\U7684\Uff0c\U63d0\U4ea4\U6c11\U653f\U90e8\U95e8\U9881\U53d1\U7684\U300a\U9769\U547d \U4f24\U6b8b\U519b\U4eba\U8bc1\U300b\U4ee5\U53ca\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U4f5c\U51fa\U7684\U65e7\U4f24\U590d\U53d1\U7684\U9274\U5b9a\U8bc1\U660e,\U804c\U5de5\U65e7\U4f24\U590d\U53d1\U7684\Uff0c\U63d0\U4ea4\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U4f5c\U51fa\U7684\U300a\U5de5\U4f24\U8ba4\U5b9a\U51b3\U5b9a\U4e66\U300b\U4ee5\U53ca\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U4f5c\U51fa\U7684\U65e7\U4f24\U590d\U53d1\U7684\U9274 \U5b9a\U7ed3\U8bba,\n(\U4e94)\U5de5\U4f5c\U671f\U95f4\U7a81\U53d1\U75be\U75c5\U6b7b\U4ea1\U7684\Uff0c\U63d0\U4ea4\U533b\U7597\U673a\U6784\U7684\U6b7b\U4ea1\U8bc1\U660e\U4e66,\n(\U516d)\U56e0\U5de5\U4f5c\U5916\U51fa\U671f\U95f4\Uff0c\U53d1\U751f\U4e8b\U6545\U4e0b\U843d\U4e0d\U660e\U9700\U8ba4\U5b9a\U56e0\U5de5\U6b7b\U4ea1\U7684\Uff0c\U63d0\U4ea4\U4eba\U6c11\U6cd5\U9662\U5ba3\U544a\U6b7b\U4ea1\U7684\U5224 \U51b3\U4e66\U3002\n\U7b2c\U5341\U4e8c\U6761\U3000\U7533\U8bf7\U4eba\U63d0\U4f9b\U7684\U6750\U6599\U4e0d\U5b8c\U6574\U7684\Uff0c\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U5e94\U5f53\U572815\U65e5 \U5185\Uff0c\U4e00 \U6b21\U6027\U4e66\U9762\U544a\U77e5\U7533\U8bf7\U4eba\U9700\U8981\U8865\U6b63\U7684\U5168\U90e8\U6750\U6599\U3002\U7533\U8bf7\U4eba\U5e94\U5f53\U4ece\U544a\U77e5\U4e4b\U65e5\U8d7730\U65e5\U5185\U8865\U6b63\U5168\U90e8\U6750\U6599\U3002\n\U6309\U6cd5\U5b9a\U7a0b\U5e8f\U5904\U7406\U52b3\U52a8\U5173\U7cfb\U4e89\U8bae\U7684\U65f6\U95f4\U4e0d\U8ba1\U7b97\U5728\U5de5\U4f24\U8ba4\U5b9a\U7684\U65f6\U9650\U5185\U3002\n\U7b2c\U5341\U4e09\U6761\U3000\U7701\U548c\U8bbe\U533a\U5e02\U7684\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U5e94\U5f53\U5efa\U7acb\U533b\U7597\U536b\U751f\U4e13\U5bb6\U5e93 \U3002\U7701\U548c\U8bbe\U533a\U5e02\U7684\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U529e\U4e8b\U673a\U6784\U8bbe\U5728\U540c\U7ea7\U52b3\U52a8\U4fdd\U969c\U884c\U653f\U90e8\U95e8\U3002\n\U7b2c\U5341\U56db\U6761\U3000\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U5305\U62ec\U4ee5\U4e0b\U5185\U5bb9\Uff1a\n(\U4e00) \U52b3\U52a8\U529f\U80fd\U969c\U788d\U7a0b\U5ea6\U7b49\U7ea7\U7684\U9274\U5b9a,\n(\U4e8c)\U751f\U6d3b\U81ea\U7406\U969c\U788d\U7a0b\U5ea6\U7b49\U7ea7\U7684\U9274\U5b9a,\n(\U4e09)\U505c\U5de5\U7559\U85aa\U671f\U5ef6\U957f\U7684\U786e\U8ba4,\n(\U56db)\U5de5\U4f24\U76f4\U63a5\U5bfc\U81f4\U75be\U75c5\U7684\U786e\U8ba4,\n(\U4e94)\U914d\U7f6e\U8f85\U52a9\U5668\U5177\U7684\U786e\U8ba4,\n(\U516d)\U4f9b\U517b\U4eb2\U5c5e\U5b8c\U5168\U4e27\U5931\U52b3\U52a8\U80fd\U529b\U7684\U9274\U5b9a,\n(\U4e03)\U6cd5\U5f8b\U3001\U6cd5\U89c4\U89c4\U5b9a\U7684\U5176\U4ed6\U4e8b\U9879\U3002\n\U7b2c\U5341\U4e94\U6761\U3000\U7533\U8bf7\U4eba\U5411\U8bbe\U533a\U7684\U5e02\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U59d4\U5458\U4f1a\U63d0\U51fa\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U7533\U8bf7\U65f6\Uff0c\U5e94\U5f53\U63d0\U4ea4\U5de5\U4f24\U8ba4 \U5b9a\U51b3\U5b9a\U3001\U8bca\U65ad\U8bc1\U660e\U4e66\U3001\U68c0\U67e5\U7ed3\U679c\U3001\U8bca\U7597\U75c5\U5386\U7b49\U8d44\U6599\U3002\U7533\U8bf7\U4eba\U8ba4\U4e3a\U5de5\U4f24\U76f4\U63a5\U5bfc\U81f4\U5176\U4ed6\U75be\U75c5\U7684\Uff0c\U8fd8\U5e94\U5f53\U63d0\U4ea4\U5de5\U4f24\U533b\U7597\U673a\U6784\U51fa\U5177\U7684\U76f8\U5173\U8bc1\U660e\U3002\n\U5341\U516d\U81f3\U4e8c\U5341\U6761\n\U7b2c\U5341\U516d\U6761\U3000\U5de5\U4f24\U804c\U5de5\U7684\U521d\U6b21\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\U3001\U5de5\U4f24\U76f4\U63a5\U5bfc\U81f4\U75be\U75c5\U786e\U8ba4\U3001\U914d\U7f6e\U8f85\U52a9\U5668\U5177\U786e\U8ba4\U548c\U4f9b\U517b \U4eb2\U5c5e\U5b8c\U5168\U4e27\U5931\U52b3\U52a8\U80fd\U529b\U9274\U5b9a\Uff0c\U5176\U8d39\U7528\U7531\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8,\U518d\U6b21\U9274\U5b9a\U6216\U590d\U67e5\U9274\U5b9a\U7684\U8d39\U7528,\U9274 \U5b9a\U7ed3\U8bba\U672a\U6539\U53d8\U7684\U7531\U7533\U8bf7\U4eba\U8d1f\U62c5, \U9274\U5b9a\U7ed3\U8bba\U6539\U53d8\U7684\U7531\U5de5\U4f24\U4fdd\U9669\U57fa\U91d1\U652f\U4ed8,\U505c\U5de5\U7559\U85aa\U671f\U5ef6\U957f\U786e\U8ba4\U7684\U8d39\U7528\Uff0c\U7531\U7528\U4eba\U5355\U4f4d\U627f\U62c5\U3002\U672a\U53c2\U52a0\U5de5\U4f24\U4fdd\U9669\U6216\U672a\U8db3\U989d\U4ea4\U7eb3\U5de5\U4f24\U4fdd\U9669\U8d39\U7684\Uff0c\U9274\U5b9a\U8d39\U7528\U7531\U7528\U4eba\U5355\U4f4d\U627f\U62c5\U3002\n\U52b3\U52a8\U9274\U5b9a\U6536\U8d39